import React, { Component } from 'react'
import './style.scss';

export default class Hero extends Component {
  render() {
    const { id, blogTitle, subtitle, blogImage, author, blogText, postedOn } = this.props.blog;
    return (
      <div className="hero">
        <div className="hero__background-image">
        <div className="hero__background-text">
            <p>I publish a blog every week on general software developer stuff and computer science.</p>
            <h2>Enjoy !!</h2>
        </div>
        </div>
      </div>
    )
  }
}
