import React, { Component } from 'react';
import { BlogsArray ,singularBlog} from '../Data';

const ProductContext = React.createContext();

 class ProductProvider extends Component {
   state = {
     //All data initialized 
     blogs:[],
     singularBlog:singularBlog


   };
   componentDidMount() {
     this.setBlogs();
   }
   setBlogs = () => {
     let tempBlogs = [];
     BlogsArray.forEach(blog => {
       const singleBlog = { ...blog };
       tempBlogs = [...tempBlogs, singleBlog];
     });
     this.setState(() => {
       return { blogs: tempBlogs };
     });
   };

   getBlog = id => {
     const blog = this.state.blogs.find(item => item.id ===id);
     return blog;
   }
   handleDetail = id =>{
     const blog = this.getBlog(id);
     this.setState(()=>{
       return {singularBlog:blog}
     })
   }
   addToCart = ()=>{
    console.log('hello from addToCart');
  }
  render() {
    return (
      <ProductContext.Provider 
      value={{
        ...this.state,
        handleDetail: this.handleDetail,
        addToCart: this.addToCart
      }}>
        {this.props.children}
      </ProductContext.Provider>
    );
  }
}

const ProductConsumer = ProductContext.Consumer;

export { ProductProvider, ProductConsumer};
