import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './style.scss';
import { ProductConsumer } from '../Context/context';
import Hero from '../Hero';
import Banner from '../Banner';

export default class Blogpage extends Component {
  render() {
    // const { id, blogTitle, subtitle, blogImage, author, blogText, postedOn } = this.props.blog;
    return (
      <ProductConsumer>
        {(value) => {
          const { id, blogTitle, subtitle, blogImage, author, blogText, postedOn } = value.singularBlog;
          return (
            <>
             <Hero>
             <Banner title="The title should load here" subtitle="subtitle should load here">
             <Link to='/about' className="btn-primary">
            portfolio
             </Link>
             </Banner>
             </Hero>
            <div className="container">
            <h1>{blogTitle}</h1>
              <div>
                <img src={blogImage} className="img-fluid" alt="image"/>
              </div> 

              <h3>{author}</h3>
              <p>{blogText}</p>
            </div>
            </>
          );
        }}
      </ProductConsumer>

    );
  }
}
