import React from 'react';
import Hero from "../Hero";
import Blog from '../Blog';
import Banner from '../Banner';
import {Link} from 'react-router-dom';
import {ProductConsumer} from '../Context/context';
import "./style.scss";

export default function Services ({name, title}) {

    return (
      <>
      <Hero>
       <Banner title="I Offer the following services to my clients" /*subtitle="I am Looking foward to working with you"*/>
          <Link to='/contact' className="btn-primary">
            Hire Me
          </Link>
        </Banner>
      </Hero>

      <div>
    {/* render worksingle here */}
        <React.Fragment>
        <div className="works-display">
          <ProductConsumer>
            {value => {
              return value.projectData.map(project => {
                return <Worksingle key={project.id} project={project} />
              })
            }}
          </ProductConsumer>
        </div>
        </React.Fragment> 
      </div>

      </>
    )
}
