import React from 'react';
import Hero from "../Hero";
import Banner from '../Banner';
import {Link} from 'react-router-dom';
import Worksingle from '../Worksingle';
import {ProductConsumer} from '../Context/context';
import "./style.scss";

export default function Work ({name, title}) {

    return (
    <>
      <Hero>
        <Banner title="My Services" subtitle="I offer the following services to my clients">
          <Link to='/about' className="btn-primary">
            portfolio
          </Link>
        </Banner>
      </Hero>
      <div>
    {/* render worksingle here */}
        <React.Fragment>
        <div className="works-display">
          <ProductConsumer>
            {value => {
              return value.serviceData.map(service => {
                return <Service key={service.id} service={service} />
              })
            }}
          </ProductConsumer>
        </div>
        </React.Fragment> 
      </div>
    </>
    )
}
