import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './style.css';
import { ProductConsumer } from '../Context/context';

export default class Snack extends Component {
  render() {
    const { id, title, img, inCart, price } = this.props.snack;
    return (
      <div className="card">
          <div className="snack-img" 
          onClick={()=> console.log("u want to add to cart??")}>
            <img src={img} alt="snack" class="snack-img__image"/>
          </div>
          {/* card footer */}
          <div className="card-footer">
            <p className="title">
              {title}
            </p>
            <h5 className="price">
              <span >Ksh</span>
            </h5>
          </div>
      </div>
    )
  }
}
