import { Link } from 'react-router-dom';
import './style.scss';
import { ProductConsumer } from '../Context/context';


export default class Service extends Component {
    render() {
      const { id, serviceTitle, serviceDescription, serviceImage } = this.props.service;
      return (
            <ProductConsumer>
            {(value) =>(

                <div className="service" >
                <h3 className="serviceTitle">{serviceTitle}</h3>
                <div className="img-container">
                  <img src={serviceImage} alt="project" className="img-container-inner"/>
                </div>
                <p className="projectDescription">{serviceDescription}</p>
                {/* button goes here */}
                <div className="btn-group">
                {/* <Link to='/about' className="btn-primary">
                live
                 </Link>
                 <Link to='/about' className="btn-primary">
                code
                 </Link> */}
                 <Link to='/about' className="btn-primary">
                    hire me
                 </Link>
                </div>
                </div>
            )}
            </ProductConsumer>
      );
    }
  }
