import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './style.scss';
import { ProductConsumer } from '../Context/context';
import PropTypes from "prop-types";

export default class Blog extends Component {
  render() {
    const { id, blogTitle, subtitle, blogTime, author, blogText, blog, blogIntro} = this.props.blog;
    return (
          <ProductConsumer>
          {(value) =>(
              <div className="blog" 
              onClick={()=> value.handleDetail(id)}>
              <Link to="/Blogpage" className="Links">
              <h2>{blogTitle}</h2>
              <h3>{subtitle}</h3>
              <h4>{author}</h4>
              <h6>{blogTime}</h6>
              <div className="img-container-inner">
                  <img src={blogImage} alt="project" className="img-container-inner"/>
              </div>
              <p>{blogIntro}</p>
              <Link to='/Blogpage' className="btn-primary">
                  Read More
              </Link>
              </Link>
              </div>
          )}

          </ProductConsumer>

    );
  }
}

Blog.propTypes = {
  blog:PropTypes.shape({
    id:PropTypes.number,
    blogTitle:PropTypes.string,
    subtitle:PropTypes.string,
    author:PropTypes.string,
    postedOn:PropTypes.string,
    blogText:PropTypes.string,
    blogImage:PropTypes.string,
    blogCategory:PropTypes.string
}).isRequired
};
